package com.syntax.class03.homework;

public class PositiveAndNegativeNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int number=10;
			
	if (number>0) //this means if the number is less than zero
	{
		System.out.println("Number is positive");
	}
	else if (number<0) //this means if number is less than zero
	{
		System.out.println("Number is negative");
	}
	else //that is if number is Zero
	{
		System.out.println("Number is neither positive nor negative");
	}
	
		
		
		
		
	}

}
