package com.syntax.class07.Homework;

public class WhileLoop3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Print odd numbers from 100 to 1
		int b = 100;

		while (b >= 1) {

			if (b % 2 == 1) {

				System.out.print(b + " ");
			}
			b--;
		}
		System.out.println("--------------");
		System.out.println("Another way---------------------");

		int c = 99;

		while (c>=1) {

			System.out.print(c+ " ");
			c -=2;
		}	
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}
}
