package com.syntax.loops;

public class DoWhileLoop2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Print even numbers from 10 to 20. Must not include 20
		
				int num=10;
				
				do {
					System.out.println(num);
					num++;
				}while(num<=19);	
		
		
		
		
		
		
		
	}

}
