package com.syntax.loops;

public class DoWhileLoop3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	//Print even numbers from 20 to 1
		
		int evenNumber=20;
		
		do {
			System.out.println(evenNumber);
			evenNumber-=2;
		}while(evenNumber>=1);
		
		
		
		
		
	}

}
