package com.syntax.loops;

public class ForLoop3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		
		for(int i=0; i<=10; i+=2) {
			System.out.print(i+" ");
		}

		
		
		System.out.println();
		
		for(int a=10; a<=25; a+=5) {
			System.out.print(a+" ");
		}

	}

}
