package com.syntax.loops;

public class IncrementAndDecrement {
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// There are multiple ways to increment or decrement the value of a variable
		//This way is used in loop
		
		// To increment the value of a variable
		int box = 90;

		box++;// 91
		System.out.println(box);

		box--;// 90
		System.out.println(box);

		int num = 100;

		num++;// 101
		System.out.println(num);

		// To decrement the value of a variable

		int num2 = 50;
		num2--;//49
		System.out.println(num2);

		int i = 10;
		i--;// 9
		i--;// 8
		i++;// 9
		System.out.println(i);

	}
}
