package com.syntax.loops;

public class WhileLoop5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	//Print all numbers from 50 to 20
		
		int num =50;
		
		while(num>=20) {
			
			System.out.print(num+" ");
			num--;
		}
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
