package com.syntax.stringandarthopertors.homework;

public class ArthmeticOperators2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		float num1=8.2f;
		float num2=8.2f;
		
		float addition=num1+num2;
		float subtraction=num1-num2;
		float multiplication=num1*num2;
		float division=num1/num2;
		
		
		
		System.out.println("The addition "+"of 2 numbers "+num1+" and "+num2+" is "+" equal "+addition);
		System.out.println("The subtraction "+"of 2 numbers "+num1+" and "+num2+" is "+" equal "+subtraction);
		System.out.println("The multiplication "+"of 2 numbers "+num1+" and "+num2+" is "+" equal "+multiplication);
		System.out.println("The division "+"of 2 numbers "+num1+" and "+num2+" is "+" equal "+division);
		
		
		
		
		
		
		
		
		
	}

}
